names = ["IndustryCode", "PayeRefs", "VatRefs", "PostCode", "BusinessName", 
         "EmploymentBands", "Turnover", "TradingStatus", "LegalStatus", "Request"]

for (record in records) {
  try {
   requestField = names.find { record.value[it] != null }
    if (requestField == null) {
      	msg = "Can not find supported request field"
        log.error(msg)
    	error.write(record, msg)
    }
    // Request fields BusinessName and Request required special processing.
    if (requestField == 'BusinessName') {
      /* we remove all special symbols and also adding fuzzines to word longer than 3 symbols */
      bn = record.value['BusinessName'].replaceAll("[^a-zA-Z\\d\\s]", "")
      bn = URLEncoder.encode(bn, 'UTF-8')
      bn = bn.split('\\++').collect { it -> it.length() > 5 ?'(' + it + '~2)' : it }.join('+')
      record.value['dataRequest'] = 'BusinessName:(' + bn + ')?limit=3'
      record.value['countRequest'] = 'BusinessName:(' + bn + ')?limit=0'
    } else if (requestField == 'Request') {
      record.value['dataRequest'] = record.value[requestField]
      record.value['countRequest'] = record.value['dataRequest'] + '?limit=0'
    } else {
      record.value['dataRequest'] = requestField +':"' + record.value[requestField] + '"'
      record.value['countRequest'] = record.value['dataRequest'] + '?limit=0'
    }
    output.write(record)
  } catch (e) {
    // Write a record to the error pipeline
    log.error(e.toString(), e)
    error.write(record, e.toString())
  }
}

