import javax.mail.*

/**
original filepath contains real file name as root directory for file itself (see output dir building logic).
we take file name from this original path and ignore name generated by streamsets.

out of one input file might generate set of outputs, that is why we're keeping in memory indx of last file that has been processed for this name.
*/

for (record in records) {
  try {
    def path = record.value['filepath']
    def group = (path =~ /.*\/(.+)\/.*/)
    def inFileName = group[0][1].replace('.csv', '')


    def indx = 1
    if (state.containsKey(inFileName)) {
      indx = state.get(inFileName)
      state.put(inFileName,indx + 1)
    } else {
      state.put(inFileName, 1)
    }

    log.error('Is state contains ' + inFileName + ' > ' + state.containsKey(inFileName))



    record.value['newFilename'] = inFileName + '.' + indx + '.csv'  
    output.write(record)
  } catch (e) {
    log.error(e.toString(), e)
    error.write(record, e.toString())
  }
}

